""" ~ CML // Creative Machine Learning ~ data/__init__.py : Initializing data generating functions  In order to understand the fundamentals of ML, we will work on several toy datasets for different tasks that we are adressing - Regression  Author               :  Philippe Esling                        <esling@ircam.fr>"""import dataclassesfrom typing import Callable@dataclasses.dataclassclass DataGenerator():    sampling_function: Callable = None    n_observations: int = 1000    pre_generate: bool = True    batch_size: int = 1    def __generate__(self):        data = self.sampling_function(self.n_observations)        return data        def __init__(self):        pass            def __next__(self):        pass        def __get__(self):        pass